        if(single >= 0){
            setsingle(single +1);
        }
        else{
            setsingle(0);
        }
    };
    const decr = () =>{
        if(single == 0){
            setsingle("à¦†à¦° à¦•à¦¤ à¦¨à¦¿à¦šà§‡ à¦¨à¦¾à¦®à¦¬à¦¾ðŸ˜’");
        }else if(single > 0){
            setsingle(single -1);
        }else{
            setsingle(0);
        }
    };
    return(
        <>
            <div className="inc">
                <Card className="card">
                    <h1>{single}</h1>
                    <div className="btn">
                        <Button onClick={incr}><ArrowUpwardIcon/>Incre</Button>
                        <Button onClick={decr}><ArrowDownwardIcon/>Decre</Button>
                    </div>
                </Card>
            </div>
        </>
    );
};
export default Inc;
