import React, { useState } from 'react';
import './useeff.css';

const UseEff =()=>{
    const [num,setnum] =useState(0);
    const clikk = ()=>{
        setnum(num+1);
    };
    return(
        <>
            <div className="useeff">
                <p>টাইটেলে লক্ষ করো...</p>
                <button onClick={clikk}>  Click {num}</button>
            </div>
        </>
    );
};
export default UseEff;
