import { PinDropSharp } from '@material-ui/icons';
import React, { useState } from 'react';

const Creat = (props) =>{
    const [all,setall] = useState({
        title:'',
        content:''
    });
    const addevent = (event) =>{
        const {value,name} = event.target;
        setall((old)=>{
            return{
            ...old,
            [name]:value,
            };
        });
    };
    const doAdd = (taha) =>{
        taha.preventDefault();
        props.tmacin(all);
    };
return(
    <>
    <div class="card m-auto"  style={{width:'18rem'}}>
        <div class="card-body">
        <form  onSubmit={doAdd}>
            <div class="form-group">
            <input onChange={addevent} name='title' value={all.title} type="text" class="form-control" id="exampleFormControlInput1" placeholder="Title" autoComplete='off'/>
            <textarea onChange={addevent} name='content' value={all.content} class="form-control" id="exampleFormControlTextarea1" rows="1" placeholder="Type your Note..."></textarea>
            </div>
            <button class="btn btn-primary">Submit</button>
        </form>
        </div>
    </div>
    </>
);
};

export default Creat;
