import React, { useState } from 'react';

const App = () => {
    let Time = new Date().toLocaleTimeString();
    const [ctime , setstime] = useState(Time);
    const Ahar = () => {
        Time = new Date().toLocaleTimeString();
        setstime(Time);
    };
    setInterval(Ahar,1000);
    return(
        <>
        <h2>React Time</h2>
        <p>With Hooks,setInterval</p>
        <h1>{ctime}</h1>
        </>
    );
 };
export default App;
