import React, { useState } from 'react';

const App = () =>{
    let name ="Rakib";

    const evento = (bar) =>{
        console.log(bar.target.value);
        name = bar.target.value;
    };
    return(
    <div>
        <h1>Hello {name}</h1>
        <input onChange={evento} type="text" placeholder="Type Your Name"/>
        <button>Hello</button>
    </div>
    );
};
export default App;
