import React, { useState } from 'react';
import './inc.css';

const Inc = () =>{
    let multi = 0;
    const incr = () =>{
        return multi = multi+1;
    };
    const decr = () =>{
        return multi = multi -1;
        
    };
    return(
        <>
            <div className="inc">
                <div className="card">
                    <h1>{multi}</h1>
                    <div className="btn">
                        <button onClick={incr}>Incre</button>
                        <button onClick={decr}>Decre</button>
                    </div>
                </div>
            </div>
        </>
    );
};
export default Inc;
