import React, { useState } from 'react';

const App = () =>{
    let bg = '';
    const Click = "Click Me ðŸ˜";
    const [bgg,setbgg] = useState(bg);
    const [cik,setcik] = useState(Click);
    const maro = () =>{
        let bakka = 'pink';
        setbgg(bakka);
        setcik('Good Work âœŒ');
    };
    return(
    <>
    <div style={ {background : bgg}}>
        <button onClick = {maro}>{cik}</button>
    </div>
    </>
    );
};
export default App;
