import React, { useState } from 'react';

const App = () =>{
    let name ="Rakib";
    const [nama,setnama] = useState();
    const [fullname ,setfullname] = useState({
        fname:'',
        lname:'',
        email:'',
        number:''
    });

    const evento = (bar) =>{
        setnama(bar.target.value);
    };
    const getit = (akh) =>{
        akh.preventDefault();
        setfullname(nama);
    };
    return(
    <div>
        <h1>Alhamdulillah, {fullname}</h1>
        <p>{nama}</p>
        <p></p>
        <form onSubmit={getit}>
            <input onChange={evento} name='fname' type="text" placeholder="Type Your First Name"/>
            <input onChange={evento} name='lname' type="text" placeholder="Type Your Last Name"/>
            <input onChange={evento} name='email' type="email" placeholder="Type Your Email"/>
            <input onChange={evento} name='number' type="number" placeholder="Type Your Number"/>
            <button>Enter</button>
        </form>
    </div>
    );
};
export default App;
